mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM crew_member
WHERE member_id = #{memberId}
AND crew_id = #{crewId}
AND is_leader = 'Y'

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM crew_member
WHERE member_id = #{memberId}
AND crew_id = #{crewId}
AND IS_LEADER = 'N'

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM crew_member
WHERE crew_member_id = #{crewMemberId}
AND crew_id = #{crewId}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM crew_member
WHERE member_id = #{memberId}

mapper.update:
UPDATE crew_member
SET is_leader = #{isLeader},
updated_at = sysdate,
updated_by = #{loginMemberId}
WHERE crew_member_id = #{crewMemberId}
